import React, { useState } from 'react';
import { Paper, Grid, Card, Typography, Avatar, Stack } from "@mui/material";
import { styled } from '@mui/material/styles';

const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
}));


const Boards = () => {
    const [boards, setBoards] = useState([
        { id: 1, title: "Новые", items: [{ id: 1, title: "Пойти в магазин" }, { id: 2, title: "Выкинуть мусор" }] },
        { id: 2, title: "В работе", items: [{ id: 1, title: "Купить продукты" }, { id: 2, title: "Съесть сырок" }] },
        { id: 3, title: "Выполнено", items: [{ id: 1, title: "Погулять с собакой" }] }
    ]);


    const [currentBoard, setCurrentBoard] = useState(null)
    const [currentItem, setCurrentItem] = useState(null)

    function dropHandler(e, board, item) {
        e.preventDefault()
        const currentIndex = currentBoard.items.indexOf(currentItem)
        currentBoard.items.splice(currentIndex, 1)
        const dropIndex = board.items.indexOf(item)
        board.items.splice(dropIndex + 1, 0, currentItem)
        setBoards(boards.map(b => {
            if (b.id === board.id) {
                return board
            }
            if (b.id === currentBoard.id) {
                return currentBoard
            }
            return b
        }))
        e.target.style.boxShadow = 'none'
    }

    function dragOverHandler(e) {
        e.preventDefault()
        console.log(e.target.classList)
        console.log(e.target.classList.contains('item'))
        if (e.target.classList.contains('item')) {
            e.target.style.boxShadow = '0 4px 3px black'
        }
        if (e.target.parent) {
            console.log(e.target.classList)
            console.log(e.target.classList.contains('item'))
            e.target.parrent.style.boxShadow = '0 4px 3px black'
        }

    }

    function dragEndHandler(e) {
        e.target.style.boxShadow = 'none'
    }

    function dragStartHandler(e, board, item) {
        setCurrentBoard(board)
        setCurrentItem(item)
    }

    function dragLeaveHandler(e) {
        e.target.style.boxShadow = 'none'
    }

    function dropCardHandler(e, board) {
        if(e.target.classList.contains('board')) {
            board.items.push(currentItem)
            const currentIndex = currentBoard.items.indexOf(currentItem)
            currentBoard.items.splice(currentIndex, 1)
            setBoards(boards.map(b => {
                if (b.id === board.id) {
                    return board
                }
                if (b.id === currentBoard.id) {
                    return currentBoard
                }
                return b
            }))
            e.target.style.boxShadow = 'none'
        }
    }

    return (
        <Grid container spacing={2}>
            {boards.map(board =>
                <Grid item xs={4}>
                    <Card 
                        className='board'
                        onDragOver={(e) => dragOverHandler(e)}
                        onDrop={(e) => dropCardHandler(e, board)}
                        elevation={8} sx={{ backgroundColor: 'success.main' }}>
                        <Typography gutterBottom variant="h5" component="div">
                            {board.title}
                        </Typography>
                        {board.items.map(item =>
                            <Paper
                                draggable={true}
                                onDragOver={(e) => dragOverHandler(e)}
                                onDragLeave={(e) => dragLeaveHandler(e)}
                                onDragStart={(e) => dragStartHandler(e, board, item)}
                                onDragEnd={(e) => dragEndHandler(e)}
                                onDrop={(e) => dropHandler(e, board, item)}
                                className='item' 
                                elevation={3} sx={{ mx: 1, my: 2, py: 1 }}>
                                <Stack direction="row" spacing={2}>
                                    {item.title}
                                    <Avatar alt="Cindy Baker" src="/static/images/avatar/3.jpg" />
                                </Stack>
                            </Paper>)}
                    </Card>
                </Grid>
            )}
        </Grid>
    );
}

export { Boards };
