import React, { useState } from 'react';
import { Paper, Grid, Card, Typography, Avatar, Stack } from "@mui/material";
import { styled } from '@mui/material/styles';

const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === 'dark' ? '#1A2027' : '#fff',
    ...theme.typography.body2,
    padding: theme.spacing(1),
    textAlign: 'center',
    color: theme.palette.text.secondary,
}));


const Boards = () => {
    const [boards, setBoards] = useState([
        {id: 1, title: "Сделать", items: [{id: 1, title: "Пойти в магазин"}, {id: 2, title: "Выкинуть мусор"}]},
        {id: 2, title: "В работе", items: [{id: 1, title: "Пойти в магазин"}, {id: 2, title: "Выкинуть мусор"}]},
        {id: 3, title: "Выполнено", items: [{id: 1, title: "Пойти в магазин"}]}
    ]);

    return (
        <Grid container spacing={2}>
            {boards.map(board =>
                <Grid item xs={4}>
                <Card elevation={8} sx={{backgroundColor: 'success.main'}}>
                    <Typography gutterBottom variant="h5" component="div">
                    {board.title}
                    </Typography>
                    {board.items.map(item =>
                        <Paper 
                        draggable={true}
                        onDragOver={(e) => dropHandler(e, card, item)}
                        onDragLeave={(e) => dragHandler(e, card, item)}
                        onDragStart={(e) => dragStartHandler(e, card, item)}
                        onDragEnd={(e) => dragEndHandler(e, card, item)}
                        onDrop={(e) => dropHandler(e, card, item)}
                        className='item' elevation={3} sx={{ mx: 1, my: 2, py: 1 }}>
                            <Stack direction="row" spacing={2}>
                            {item.title} 
                            <Avatar alt="Cindy Baker" src="/static/images/avatar/3.jpg"/>
                            </Stack>
                        </Paper>)}
                </Card>
                </Grid>
                )}
        </Grid>
    );
}

export { Boards };
